mapper.update:
UPDATE project_table

SET organizer_no = #{organizerNo}, project_title = #{projectTitle}, project_content = #{projectContent}, project_category = #{projectCategory}, project_pic = #{projectPic}, start_date = #{startDate}, end_date = #{endDate}
WHERE project_no = #{projectNo}

mapper.select:
SELECT *
FROM project_participants
WHERE project_no = #{projectNo} and participant_no = #{participantNo}
